package com.tyme.lunar;

import com.tyme.AbstractTyme;
import com.tyme.culture.Direction;
import com.tyme.culture.Twenty;
import com.tyme.culture.star.nine.NineStar;
import com.tyme.sixtycycle.SixtyCycle;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 * 农历年
 *
 * @author 6tail
 */
public class LunarYear extends AbstractTyme {

  /**
   * 缓存{闰月:年}
   */
  protected static final Map<Integer, List<Integer>> LEAP = new HashMap<>(12);

  /**
   * 年
   */
  protected int year;

  static {
    String chars = "0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ_@";
    String[] months = "080b0r0j0j0j0C0j0j0C0j0j0j0C0j0C0j0C0F0j0V0V0V0u0j0j0C0j0j0j0j0V0C0j1v0u0C0V1v0C0b080u110u0C0j0C1v9K1v2z0j1vmZbl1veN3s1v0V0C2S1v0V0C2S2o0C0j1Z1c2S1v0j1c0j2z1v0j1c0j392H0b2_2S0C0V0j1c0j2z0C0C0j0j1c0j0N250j0C0j0b081n080b0C0C0C1c0j0N,0r1v1c1v0V0V0F0V0j0C0j0C0j0V0j0u1O0j0C0V0j0j0j0V0b080u0r0u080b0j0j0C0V0C0V0j0b080V0u080b0j0j0u0j1v0u080b1c0j080b0j0V0j0j0V0C0N1v0j1c0j0j1v2g1v420j1c0j2z1v0j1v5Q9z1v4l0j1vfn1v420j9z4l1v1v2S1c0j1v2S3s1v0V0C2S1v1v2S1c0j1v2S2_0b0j2_2z0j1c0j,,_,,,,,,,,0B0j0V0j0j0C0j0j0j0C0j0C0j0j0C0j0m0j0j0j0j0C0j0C0j0j0u0j1c0j0j0C0C0j0j0j0j0j0j0j0j0u110N0j0j0V0C0V0j0b081n080b0CrU1O5e2SbX2_1Z0V2o141v0j0C0C0j2z1v0j1c0j7N1O420j1c0j1v2S1c0j1v2S2_0b0j0V0j0j1v0N1v0j0j1c0j1v140j0V0j0j0C0C0b080u1v0C0V0u110u0j0j0j0C0j0j0j0C0C0N0C0V0j0j0C0j0j0b080u110u0C0j0C0u0r0C0u080b0j0j0C0j0j0j".split(",", -1);
    for (int i = 0; i < 12; i++) {
      int n = 0;
      String m = months[i];
      int size = m.length() / 2;
      List<Integer> l = new ArrayList<>(size);
      for (int y = 0; y < size; y++) {
        int z = y * 2;
        String s = m.substring(z, z + 2);
        int t = 0;
        int c = 1;
        for (int x = 1; x > -1; x--) {
          t += c * chars.indexOf(s.charAt(x));
          c *= 64;
        }
        n += t;
        l.add(n);
      }
      LEAP.put(i + 1, l);
    }
  }

  public LunarYear(int year) {
    if (year < -1 || year > 9999) {
      throw new IllegalArgumentException(String.format("illegal lunar year: %d", year));
    }
    this.year = year;
  }

  /**
   * 从年初始化
   *
   * @param year 年，支持-1到9999年
   * @return 农历年
   */
  public static LunarYear fromYear(int year) {
    return new LunarYear(year);
  }

  /**
   * 年
   *
   * @return 年
   */
  public int getYear() {
    return year;
  }

  /**
   * 天数
   *
   * @return 天数
   */
  public int getDayCount() {
    int n = 0;
    for (LunarMonth m : getMonths()) {
      n += m.getDayCount();
    }
    return n;
  }

  /**
   * 月数
   *
   * @return 月数
   */
  public int getMonthCount() {
    return getLeapMonth() > 0 ? 13 : 12;
  }

  /**
   * 依据国家标准《农历的编算和颁行》GB/T 33661-2017，农历年有2种命名方法：干支纪年法和生肖纪年法，这里默认采用干支纪年法。
   *
   * @return 名称
   */
  public String getName() {
    return String.format("农历%s年", getSixtyCycle());
  }

  public LunarYear next(int n) {
    return fromYear(year + n);
  }

  /**
   * 闰月
   *
   * @return 闰月数字，1代表闰1月，0代表无闰月
   */
  public int getLeapMonth() {
    if (year == -1) {
      return 11;
    }
    for (Map.Entry<Integer, List<Integer>> entry : LEAP.entrySet()) {
      if (entry.getValue().contains(year)) {
        return entry.getKey();
      }
    }
    return 0;
  }

  /**
   * 干支
   *
   * @return 干支
   */
  public SixtyCycle getSixtyCycle() {
    return SixtyCycle.fromIndex(year - 4);
  }

  /**
   * 运
   *
   * @return 运
   */
  public Twenty getTwenty() {
    return Twenty.fromIndex((int) Math.floor((year - 1864) / 20D));
  }

  /**
   * 九星
   *
   * @return 九星
   */
  public NineStar getNineStar() {
    return NineStar.fromIndex(63 + getTwenty().getSixty().getIndex() * 3 - getSixtyCycle().getIndex());
  }

  /**
   * 太岁方位
   *
   * @return 方位
   */
  public Direction getJupiterDirection() {
    return Direction.fromIndex(new int[]{0, 7, 7, 2, 3, 3, 8, 1, 1, 6, 0, 0}[getSixtyCycle().getEarthBranch().getIndex()]);
  }

  /**
   * 月份列表
   *
   * @return 月份列表，一般有12个月，当年有闰月时，有13个月。
   */
  public List<LunarMonth> getMonths() {
    List<LunarMonth> l = new ArrayList<>(13);
    LunarMonth m = LunarMonth.fromYm(year, 1);
    while (m.getYear() == year) {
      l.add(m);
      m = m.next(1);
    }
    return l;
  }
}
